import styles from "../App.module.css";
import React, { useState } from "react";
import apiClient from "../services/api-client-axios.js";
import { useEffect } from "react";
import spinner from "../assets/loadingAnimation.gif";

interface Game {
	id: number;
	name: string;
	cover: string;
}
interface FetchGamesResponse {
	count: number;
	results:Game[];
}
function GameGrid() {
	const [games, setGames ] = useState([]);
	const [error, setError] = useState("");
	const [isLoading, setLoading] = useState(false);
	let [oneGame, setOneGame] = useState([]);
	let free = []
	let game = "";

	useEffect(() => {
		apiClient.get("")
			.then(res =>  setGames(res.data))
			.catch(err => setError(err.message));
		apiClient.get("91e8c4f7-3372-4b0e-abe0-e4df2fe51ffc")
			.then(res => setOneGame(res.data))
			.catch(err => setError(err.message));
	}, [])

return (
	<>
	<ol className={styles.games}> {error} {isLoading && <img src={spinner} className={styles.load} />}  {games.map(game => (
                  <li key={game.title} className={styles.gamelists}>
                  <div className={styles.oneGame}>
                  <div><img className={styles.gameCover}src={game.cover} /></div>
                  <div className={styles.gameTitle}>{game.title}</div>
                  </div>
                  </li>))}
        </ol>
	</>
);
};

export default GameGrid;
