import styles from "./App.module.css";
import NavBar from "./components/NavBar.jsx";
import { useState } from "react";
import on from "./assets/thumbs_up.gif";
import off from "./assets/thumbs_down.gif";
import ApiClient from "./services/api-client.js";

function App() {
        const [mode, setMode] = useState(true);
	let apiData = ApiClient();

function ColorModeSwitch() {
        if (mode)
        {
                return (
                        <>

                        <div className={styles.colorMode}onClick={() => setMode(false)}> <img src={on} className={styles.onoff} />
                        </div><div className={styles.mode}>White Mode</div>
                        </>
                );
        }
        return (
                <>
                <div className={styles.colorMode}onClick={()=>setMode(true)}><img src={off} className={styles.onoff} />
                </div>
		<div className={styles.mode}>Dark Mode
		</div>
                </>
                );
};


function colorMode() {
	if (mode)
	{
		return (styles.white);
	}
	return (styles.dark);
}
  return (
  <>
	  <div className={colorMode()}>
	  	<div className={styles.nav}>
		  	<NavBar />
		  	<p className={styles.searchWord}>NavBar </p>
	  		<ColorModeSwitch />
		  </div>
		  <div className={styles.aside}>Aside</div>
		  <div className={styles.main}>
	  Main
	  <ul className={styles.gamelist}> {apiData.map(game => (
		  <li key={game.title} className={styles.gamelists}>{game.title}<img src={apiData.cover} /></li>))}
	  </ul>
	  </div>
	  </div>
  </>
  );
}

export default App;
