import useGames from "../hooks/useGames.js";
import styles from "../App.module.css";
import spinner from "../assets/loadingAnimation.gif";
import PlatformIconList from "./PlatformIconList.jsx";

function GameGrid() {
const {games, error, isLoading} = useGames();

return (
	<>
	<ol className={styles.games}> {error} {isLoading && <img src={spinner} className={styles.load} />}  {games.map(game => (
                  <li key={game.id} className={styles.gamelists}>
                  <div className={styles.oneGame}>
                  <div><img className={styles.gameCover}src={game.cover} /></div>
                  <div className={styles.gameTitle}>{game.title}
		</div>
		<div>
		<ul>
		{game.platform.map(platform => (<p className={styles.platform} key={platform.platform}> {PlatformIconList(platform.platform)} {platform.platform}</p>))}
}
		</ul>
		</div>
                 </div>
		</li>
	))};
       		 </ol>
	</>
);
};

export default GameGrid;
