import useGames from "../hooks/useGames.js";
import styles from "../App.module.css";
import spinner from "../assets/loadingAnimation.gif";
import PlatformIconList from "./PlatformIconList.jsx";
import Rating from "./Rating.jsx";
import GameCardSkeleton from "./GameCardSkeleton.jsx";

interface Props {
	selectedGenre: Genre | null,
	selectedPlatform: Platform | null
}

function GameGrid({ selectedGenre, selectedPlatform }: Props) {
const {games, error, isLoading} = useGames(selectedGenre, { params: { genres: selectedGenre?.title}}, [selectedGenre?.title]);
const skeletons = [1, 2, 3, 4, 5, 6];
	function Shower() {
		if (selectedGenre == null && selectedPlatform == null)
		{
			return(
			<>
			{games.map(game => (
	                  <li key={game.id} className={styles.gamelists}>
	                  <div className={styles.oneGame}>
	       	           <div><img className={styles.gameCover}src={game.cover} /></div>
	                  <div className={styles.gameTitle}>{game.title} {game.genra.map(genre => <div key={genre.genre}>{genre.genre}</div>)}
			</div>
			<div>
			<ul>
			{PlatformIconList(game)}
			</ul>
			<div className={styles.metacritic}>
			{Rating(game)}
			</div>
			</div>
       	          </div>
			</li>
		))};
				</>

			);
		}
		if (selectedGenre != null && selectedPlatform == null) {
			return(
			<>
			{games.map(game => (
	                  <li className={styles.gamelists}>
	                  {game.genra.map(genre => (genre.genre == selectedGenre)? 

				  <div className={styles.oneGame}>
				  <div><img className={styles.gameCover}src={game.cover} /></div>
				  <div className={styles.gameTitle}>
				  <div key={genre.genre}>
				  <div>
				  </div> 
				  {game.title}
				  </div>
				  <div>
				  {genre.genre}
				  </div>
				  </div>
				  <div>
				  <ul>{PlatformIconList(game)}</ul>
				  <div className={styles.metacritic}>{Rating(game)}</div>
				  </div>
				  </div>
				  : <div className={styles.nothing}></div>)}
			</li>
		))};
			</>

			);
		}
		if (selectedGenre == null && selectedPlatform != null) {
			return(
			<>
			{games.map(game => (
	                  <li className={styles.gamelists}>
	                  {game.platform.map(platform => (platform.platform == selectedPlatform)? 

				  <div className={styles.oneGame}>
				  <div><img className={styles.gameCover}src={game.cover} /></div>
				  <div className={styles.gameTitle}>
				  <div key={platform.platform}>
				  <div>
				  </div> 
				  {game.title}
				  </div>
				  <div>
				  {platform.platform}
				  </div>
				  </div>
				  <div>
				  <ul>{PlatformIconList(game)}</ul>
				  <div className={styles.metacritic}>{Rating(game)}</div>
				  </div>
				  </div>
				  : <div className={styles.nothing}></div>)}
			</li>
		))};
			</>

			);
		}

		if (selectedGenre != null && selectedPlatform != null) {
			return(
			<>
			{games.map(game => (
	                  <li className={styles.gamelists}>
				{game.platform.map(platform => (platform.platform == selectedPlatform)? 

				  <div className={styles.oneGame}>
				  <div><img className={styles.gameCover}src={game.cover} /></div>
				  <div className={styles.gameTitle}>
				  <div key={platform.platform}>
				  <div>
				  </div> 
				  {game.title}
				  </div>
				  <div>
				  {platform.platform}
				  </div>
				  </div>
				  <div>
				  <ul>{PlatformIconList(game)}</ul>
				  <div className={styles.metacritic}>{Rating(game)}</div>
				  </div>
				  </div>
				  : <div className={styles.nothing}></div>)}
		))};
				{game.genra.map(genre => (genre.genre == selectedGenre)? 

				  <div className={styles.oneGame}>
				  <div><img className={styles.gameCover}src={game.cover} /></div>
				  <div className={styles.gameTitle}>
				  <div key={genre.genre}>
				  <div>
				  </div> 
				  {game.title}
				  </div>
				  <div>
				  {genre.genre}
				  </div>
				  </div>
				  <div>
				  <ul>{PlatformIconList(game)}</ul>
				  <div className={styles.metacritic}>{Rating(game)}</div>
				  </div>
				  </div>
				  : <div className={styles.nothing}></div>)}
			</li>
		))};
			</>

			);
		}


	}

return (
	<>
	<ol className={styles.games}> 
	{error && <p> Ezra site request error code 77 </p>} 
	{isLoading && <img src={spinner} className={styles.load} />}  
	<Shower />
       		 </ol>
	</>
);
};

export default GameGrid;
