import React, { useState } from "react";
import Platform from "./Platform";

interface Props {
	onSelectPlatform: (platform: Platform) => void;
}

function PlatformSelector({ onSelectPlatform} : Props) {
	const platforms = Platform();
	const [selectedOption, setSelectedOption] = useState('');

	function handleOptionChange(event) {
		setSelectedOption(event.target.value);
		onSelectPlatform(event.target.value);
	};
	return (
		<>
		<h1>....</h1>
		<label htmlFor="platform"></label>
		<select id="platform" value={selectedOption} onChange={handleOptionChange}>
			<option value="">Patform</option>
			{platforms.map((option, index) => (
				<option key={index} value={option.title}>
				{option.title}
				</option>
			))}
		</select>
		<p>You selected: {selectedOption}</p>
		</>
	);
};

export default PlatformSelector;
