import styles from "./App.module.css";
import NavBar from "./components/NavBar.jsx";
import { useState } from "react";
import on from "./assets/thumbs_up.gif";
import off from "./assets/thumbs_down.gif";
import ApiClient from "./services/api-client.js";
import colorMode from "./components/colorMode.jsx";
import ColorModeSwitch from "./components/colorModeSwitch";
import spinner from "./assets/loadingAnimation.gif";
import GameFetch from "./services/gameFetch.jsx";
import { useEffect } from "react";

function App() {
        const [mode, setMode] = useState(true);
	const [error, setError] = useState("");
	const[isLoading, setLoading]= useState(false);
	let apiData = ApiClient(error, setError, isLoading, setLoading);
	let i = 0;
	while (i < 2) {
	GameFetch(apiData);
		i +=1;
	}
return (
  <>
	  <div className={colorMode(mode)}>
	  	<div className={styles.nav}>
		  	<NavBar />
		  	<p className={styles.searchWord}>NavBar </p>
	  		<ColorModeSwitch mode={mode} setMode={setMode}/>
		  </div>
		  <div className={styles.aside}>Aside</div>
		  <div className={styles.main}>
	  <div>Main</div>
	<div>
	  <ol className={styles.games}> {error} {isLoading && <img src={spinner} className={styles.load} />}  {apiData.map(game => (
		  <li key={game.title} className={styles.gamelists}>
		  <div className={styles.oneGame}>
		  <div><img className={styles.gameCover}src={game.cover} /></div>
		  <div className={styles.gameTitle}>{game.title}</div>
		  </div>
		  </li>))}
	</ol>
	</div>
	  </div>
	  </div>
  </>
  );
}

export default App;
