import { useGames } from "../hooks/useGames";
import styles from "../App.module.css";

function GameGrid() {
const {games, error, isLoading} = useGames();

return (
	<>
	<ol className={styles.games}> {error} {isLoading && <img src={spinner} className={styles.load} />}  {games.map(game => (
                  <li key={game.id} className={styles.gamelists}>
                  <div className={styles.oneGame}>
                  <div><img className={styles.gameCover}src={game.cover} /></div>
                  <div className={styles.gameTitle}>{game.title} 
		<ul>
		{game.platform.map(platform => (<li key={platform.platform}>{platform.platform}</li>))}
		</ul>
		</div>
                  </div>
                  </li>))}
        </ol>
	</>
);
};

export default GameGrid;
