import { useEffect } from "react";

const [apiData, fetchApi] = useState([]);
const [error, setError] = useState("");

function Api() {
	useEffect(() => {
		setLoading(true);
		document.title = "RAWG Ezra Games";
		connect();
		x();
		return() => disconnect();
	}, [])
	return (apiData);
};

const x = async() => {
	try {
		const result = await fetch("http://127.0.0.1:8000/");
		const finale = await result.json();
		console.log(finale);
		fetchApi(finale);
		setLoading(false);
	}
	catch(err) {
		setError(err.message);
		setLoading(false);
	}
};

export default Api;
