import styles from "./App.module.css";
import NavBar from "./components/NavBar.jsx";
import { useState } from "react";
import on from "./assets/Switch_on.jpg";

function App() {
        const [mode, setMode] = useState(true);

function ColorModeSwitch() {
        if (mode)
        {
                return (
                        <>

                        <div className={styles.colorMode}onClick={() => setMode(false)}> <img className={styles.onoff} src={on} />
                        </div><div className={styles.mode}>White Mode</div>
                        </>
                );
        }
        return (
                <>
                <div onClick={()=>setMode(true)}>
                </div>
		<div className={styles.mode}>Dark Mode
		</div>
                </>
                );
};


function colorMode() {
	if (mode)
	{
		return (styles.white);
	}
	return (styles.dark);
}
  return (
  <>
	  <div className={colorMode()}>
	  	<div className={styles.nav}>
		  	<NavBar />
		  	<p className={styles.searchWord}>NavBar </p>
	  		<ColorModeSwitch />
		  </div>
		  <div className={styles.aside}>Aside</div>
		  <div className={styles.main}>Main</div>
	  </div>
  </>
  );
}

export default App;
